import React from "react";
import ReactDOM from "react-dom";
import { getSports} from "./api";
import ArticleList from "./components/articleList";
import SearchBar from "./components/searchBar";
import { Container, Header } from "semantic-ui-react";
import { Button } from '@material-ui/core';
import {
  createMuiTheme,
  responsiveFontSizes,
  MuiThemeProvider,
  Typography
} from "@material-ui/core";
import './App.css';
let theme = createMuiTheme();
theme = responsiveFontSizes(theme);

const heading = "OUR WORK";
const text =
  "We work with farmers on paddy fields to understand  their problems and work backwards from there. It helps us capture and tackle that problem with the software in much organic way.";

class App extends React.Component {
  state = {
    articles: [],
    searchTopic: "",
    totalResults: "",
    loading: false,
    apiError: ""
  };

  constructor(props) {
    super(props);
    this.fileChangedHandler = this.fileChangedHandler.bind(this);
}
fileChangedHandler(event) {
  var fileInput = false
  if(event.target.files[0]) {
      fileInput = true
  }
  if(fileInput) {
      Resizer.imageFileResizer(
          event.target.files[0],
          300,
          300,
          'JPEG',
          100,
          0,
          uri => {
              console.log(uri)
          },
          'blob',
          200,
          200,
      );
  }
}


  searchForTopic = async topic => {
    try {
      this.setState({ loading: true });
      const response = await getSports(topic);
      this.setState({
        articles: response.articles,
        searchTopic: topic,
        totalResults: response.totalResults
      });
    } catch (error) {
      this.setState({ apiError: "Could not find any articles" });
    }
    this.setState({ loading: false });
  };

  render() {
    const {
      articles,
      apiError,
      loading,
      searchTopic,
      totalResults
    } = this.state;
    return (

      <Container>
        <MuiThemeProvider theme={theme}></MuiThemeProvider>
        <Header as="h2" style={{ textAlign: "center", margin: 20 }}>
        </Header>
        <input type="file" onChange={this.fileChangedHandler}/>
        <SearchBar searchForTopic={this.searchForTopic} />
        <p style={{ textAlign: "center" }}>
          Powered by <a href="https://newsapi.org/">NewsAPI.org</a>
        </p>
        {loading && (
          <p style={{ textAlign: "center" }}>Fetching your daily scoop...</p>
        )}
        {articles.length > 0 && (
          <Header as="h4" style={{ textAlign: "center", margin: 10 }}>
            Found {totalResults} articles on "{searchTopic}"
          </Header>
        )}
        {articles.length > 0 && <ArticleList articles={articles} />}
        {apiError && <p>Could not fetch any articles. Please try again.</p>}

      </Container>
    );
  }
}

export default App;
